import React from "react";
import Slider from "react-slick";
//import MembershipDetail from "./OldMembershipDetail";
import { membershipDetail } from "../../static/data";
import Button from "../common/Button";

const settings = {
  dots: false,
  swipe: true,
  touchMove: true,
  infinite: false,
  arrows: false,
  speed: 500,
  slidesToShow: 1.25,
  slidesToScroll: 0.5,
  centerMode: true,
  centerPadding: "4px"
};

function MembershipPlans() {
  return (
    <div className="container">
      <div className="heading">
        <h3>Upgrade your account</h3>

        <strong>
          Membership <span> Plans</span>{" "}
        </strong>
      </div>

      <div className="slider" {...settings}>
        {membershipDetail.map(function(
          {
            iconColor,
            list,
            backgroundColor,
            buttonColor,
            textColor,
            title,
            borderColor
          },
          index
        ) {
          return (
            <div style={{ backgroundColor }} className="card">
              <span
                className="card-heading"
                style={{ color: textColor, borderColor }}
              >
                {title}
              </span>

              {list.map(function({ text, iconColor, textColor }) {
                return (
                  <div style={{ height: 34 }}>
                    <span style={{ color: iconColor }}>icon</span>
                    <span className="text" style={{ color: textColor }}>
                      {text}
                    </span>
                  </div>
                );
              })}
              <div style={{ textAlign: "center" }}>
                <Button
                  textColor={textColor}
                  color={buttonColor}
                  width="75%"
                  height="30px"
                  content="browse membership"
                  onPress={() => console.log("pressed")}
                />
              </div>
            </div>
          );
        })}
      </div>

      <style jsx>{`
        .container {
          padding: 42px 0px;
          background-image: url("../../static/images/people_together_back.jpg");
          background-repeat-x: repeat;
          background-position: left bottom;
          background-size: contain;
        }

        .slider {
          display: flex;
          overflow-x: scroll;
          padding: 0px 24px;
          margin-top: 20px;
        }
        .heading {
          padding: 0px 24px;
        }

        h3 {
          font-size: 12px;
          font-weight: 700;
          color: #8a9db0;
          margin-bottom: 4px;
          letter-spacing: 1.5px;
          text-transform: uppercase;
        }
        span {
          color: black;
        }
        strong {
          font-size: 20px;
          color: #d9475c;
        }

        .card {
          box-shadow: 0 2px 6px -1px rgba(38, 48, 140, 0.2);
          border-radius: 4px;
          margin-right: 15px;
          padding: 24px;
          min-width: 70%;
        }

        .text {
          font-size: 14px;
          font-weight: 500;
          padding-left: 8px;
        }

        .card-heading {
          font-size: 20px;
          color: #14202b;
          font-weight: 500;
          border-top: 2px solid;
          padding: 0.5rem 0rem;
          display: inline-block;
        }
      `}</style>
    </div>
  );
}

export default MembershipPlans;
